import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as a,c as i,f as r}from"./app-53bc0d3f.js";const t={},o=r('<h4 id="_1-问题-flume任务故障-文件重命名数据丢失" tabindex="-1"><a class="header-anchor" href="#_1-问题-flume任务故障-文件重命名数据丢失" aria-hidden="true">#</a> 1. 问题-Flume任务故障，文件重命名数据丢失</h4><p>我们常用TailSource来监听日志文件，被监听的日志文件是常配置了按时间滚动生成方式的，也就是一天一个文件，到零点时会将gather.log更名为gather-20230415.log。</p><p>由于 tailSource 是根据 iNode + 文件名称组成唯一键进行监听，并记录偏移量的；</p><p>假设，设置Flume任务只监听 gather.log（也可以监听整个日志文件夹，但有问题，看完下面就明白） ，</p><p>Flume采集任务在 2023-04-15的22点出现故障，任务挂掉了，运维人员在第二天上午10点才发现并重启服务；</p><p>重启服务后，任务从偏移量记录表中找到iNode + gather.log 对应的偏移量，但是日志文件中新生成了 gather.log，原来的已经更名为gather-20230415.log，</p><p>任务就重新生成一组唯一键，进行记录偏移量，从头开始采集新的 gather.log 。</p><p>这样新数据是可以正常采集的，但是 gather-20230415.log 里的在 22点到 00点 产生的数据我们就没有采集到，这样就产生数据丢失。</p><p>如果设置监听文件夹下全部文件，上面情况是不是就导致数据重复。</p><p>修复后就直接设置为采集文件夹下的内容(使用正则)</p><h4 id="_2-修复" tabindex="-1"><a class="header-anchor" href="#_2-修复" aria-hidden="true">#</a> 2. 修复</h4><p>修改 唯一键 只使用iNode，剔除文件名称。</p><p>项目结构：</p><figure><img src="https://static-resource-yang.oss-cn-shenzhen.aliyuncs.com/typora_pic/202304151514283.png" alt="image-20230415151445251" tabindex="0" loading="lazy"><figcaption>image-20230415151445251</figcaption></figure><p>org.apache.flume.source.taildir.TailFile</p><figure><img src="https://static-resource-yang.oss-cn-shenzhen.aliyuncs.com/typora_pic/202304151541756.png" alt="image-20230415154116727" tabindex="0" loading="lazy"><figcaption>image-20230415154116727</figcaption></figure><p>org.apache.flume.source.taildir.ReliableTaildirEventReader</p><figure><img src="https://static-resource-yang.oss-cn-shenzhen.aliyuncs.com/typora_pic/202304151541571.png" alt="image-20230415154124527" tabindex="0" loading="lazy"><figcaption>image-20230415154124527</figcaption></figure><p>打包</p><p>替换 ${FLUME_HOME}/lib/flume-taildir-source-1.9.0.jar</p>',20),c=[o];function l(p,n){return a(),i("div",null,c)}const s=e(t,[["render",l],["__file","15_Flume之TailSource源码修改(修复Flume任务故障文件重命名数据丢失).html.vue"]]);export{s as default};
